
function cartoon(a) {
    var d = $("#mid").val();
    var c = $("#sta").val();
    if(null != d && "" != d){
        if(c == '2'){//账号处于激活状态
            addCart(a);
            if ($(".shoppingCartRightFix").is(":visible")) {
                a = $("#img_" + a);
                var f = a.clone().css("opacity", .75);
                $("body").append(f);
                f.css({
                    "z-index": 9E3,
                    display: "block",
                    position: "absolute",
                    top: a.offset().top + "px",
                    left: a.offset().left + "px",
                    width: a.width() + "px",
                    height: a.height() + "px"
                });
                f.animate({
                    top: $(".shoppingCartRightFix").offset().top,
                    left: $(".shoppingCartRightFix").offset().left,
                    width: 40,
                    height: 26
                }, "slow", function () {
                    f.remove()
                })
            }
        }else if(c == '3'){//账号处于冻结状态
            layer.msg('账号处于冻结状态,请联系管理员解封',{offset: '300px',time: 2000,icon: 7});
        }
    }else{
        go2Login();
    }
    
    
}
