$(function () {
    $(window).scroll(function () {
        200 < $(window).scrollTop() ? $(".r-fixed").css({
            visibility: "visible"
        }) : $(".r-fixed").css({
            visibility: "hidden"
        })
    });
    $(".c_contact_icon").hover(function () {
        var a = $(this).index(".c_contact_icon");
        $(".pophover-content").hide();
        $(".pophover-content").eq(a).show()
    }, function () {
        $(".pophover-content").hide()
    });
    $("#back").click(function () {
        $(document.documentElement).animate({
            scrollTop: 0
        }, 200);
        $(document.body).animate({
            scrollTop: 0
        }, 200)
    });
});
