$(function () {
    $(window).scroll(function () {
        200 < $(window).scrollTop() ? $(".r-fixed").css({
            visibility: "visible"
        }) : $(".r-fixed").css({
            visibility: "hidden"
        })
    });
    $("#back").click(function () {
        $(document.documentElement).animate({
            scrollTop: 0
        }, 200);
        $(document.body).animate({
            scrollTop: 0
        }, 200)
    });
});
