function cartoon(a) {
    //localStorage.removeItem("cart");
    var d = $("#mid").val();
    var c = $("#sta").val();
    if(null != d && "" != d){
        if(c == '2'){//账号处于激活状态
            addCart(a);
            if ($(".shoppingCartRightFix").is(":visible")) {
                var b = $("#img_" + a),
                    f = b.clone().css("opacity", .75);
                $("body").append(f);
                f.css({
                    "z-index": 9E3,
                    display: "block",
                    position: "absolute",
                    top: b.offset().top + "px",
                    left: b.offset().left + "px",
                    width: b.width() + "px",
                    height: b.height() + "px"
                });
                var b = $(".shoppingCartRightFix").offset().top,
                    e = $(".shoppingCartRightFix").offset().left;
                f.animate({
                    top: b,
                    left: e,
                    width: 40,
                    height: 26
                }, "slow", function () {
                    f.remove()
                })
            }
        }else if(c == '3'){//账号处于冻结状态
            layer.msg('账号处于冻结状态,请联系管理员解封',{offset: '300px',time: 2000,icon: 7});
        }
    }else{
        go2Login();
    }
    
}


