$(function () {
    $("#c_money_type").hover(function () {
        $("dl.c_account_money").show()
    }, function () {
        $("dl.c_account_money").hide()
    });
    $(window).resize(function () {
        var b = $("body").height(),
            c = $(window).width(),
            a = $(window).height();
        $(".c_exchange_bg").css({
            height: b + "px"
        });
        $(".c_recharge_remind").css({
            left: (c - 434) / 2 + "px",
            top: (a - 290) / 2 + "px"
        });
        $(".c_exchange_box").css({
            top: (a - 244) / 2 + "px"
        })
    });
    $(window).resize();
    $(".c_exchange_new_close").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_exchange_coin").hide()
    });
    $(".c_cancel").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_exchange_coin").hide()
    });
    $(".c_main_cash").click(function () {
        $(".c_exchange_bg").show();
        $(".c_exchange_coin").show()
    });
    $(".c_exchange_add_close").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_exchange_box").hide()
    });
    $(".c_exchange_ture").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_exchange_box").hide()
    })
});