
$(function () {
    $(window).resize(function () {
        var a = $("body").height(),
            b = $(window).width(),
            d = $(window).height();
        $(".c_exchange_bg").css({
            height: a + "px"
        });
        $(".c_address_in").css({
            left: (b - 784) / 2 + "px",
            top: (d - 492) / 2 + "px"
        })
    });
    $(window).resize();
    $(".c_add_address_btn").click(function () {
        $(".c_exchange_bg").show();
        $(".c_address_in").show();
        clean();
        $(".c_save").attr("onclick", "saveAres();");
    });
    $(".c_em_close").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_address_in").hide();
        
    });
    $(".c_save_other").click(function () {
        $(".c_exchange_bg").hide();
        $(".c_address_in").hide();
        
    })
});

function clean() {
    $(".c_khname").val("");
    $(".c_details_address").val("");
    $(".c_details_mobile").val("");
    $("#street").val("");
    $("#province option:selected").text("--请选择省--");
    $("#city option:selected").text("--请选择市--");
    $("#area option:selected").text("--请选择区/县--");
    $("#isdefault").removeAttr("checked");
}